import React from "react";

export default function ItemForm({ addItem }) {
  const handleAdd = () => {
    addItem({
      id: "4",
      name: "Item ",
      description: "Description for item ",
    });
  };

  return (
    <div>
      <button onClick={() => handleAdd()}>Add</button>
    </div>
  );
}
