import React, { useEffect, useState } from "react";
import { useApi } from "./contexts/ApiProvider";
import { useFlash } from "./contexts/FlashProvider";

export default function ItemForm({ addItem, updateItem, selectedItem }) {
  const api = useApi();
  const flash = useFlash();

  const [item, setItem] = useState({});
  const handleSubmit = async (e) => {
    e.preventDefault();
    let response;
    if (item._id) {
      response = await api.put(`/items/${item._id}`, item);
      updateItem(response);
      setItem({});
    } else {
      // Save to DB
      response = await api.post(`/items`, item);
      addItem(response);
      setItem({});
    }

    // Show notification on Success or error
    if (!response.error) {
      flash(`Enregistrement effectué`, "success");
      // Add item to Table
    } else {
      flash(`${response.error}`, "danger");
    }
  };

  useEffect(() => {
    setItem(selectedItem);
  }, [selectedItem]);

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="name">Nom</label>
          <input
            id="name"
            name="name"
            type="text"
            value={item.name}
            onChange={(e) => setItem({ ...item, name: e.target.value })}
          />
        </div>
        <div>
          <label htmlFor="description">Description</label>
          <input
            id="description"
            name="description"
            type="text"
            value={item.description}
            onChange={(e) => setItem({ ...item, description: e.target.value })}
          />
        </div>

        <button>Add</button>
      </form>
    </div>
  );
}
